new GadgetDescription { Id = Guid.Parse("a7be40dd-9625-4127-92ef-6debdc745bff"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Với hệ thống micrô kép tích hợp công nghệ giảm tiếng ồn AI, tai nghe có thể chống lại tiếng ồn do gió ở vận tốc 6 m/giây, đảm bảo cuộc gọi luôn rõ ràng và mạch lạc. Bên cạnh đó, ống tai nghe Xiaomi được thiết kế độc đáo cùng với thuật toán khử tiếng ồn do Xiaomi phát triển, mang lại cảm giác tự nhiên như đang trò chuyện trực tiếp với người đối diện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
