new GadgetDescription { Id = Guid.Parse("f2bf46d0-d7a3-4c61-b805-34b935ae62ec"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Thiết kế trẻ trung cùng màu sắc thanh lịch", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a3874bd4-f871-4e9f-ac9a-03a7271d469c"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Buds 6 Lite sở hữu thiết kế nhỏ gọn và hiện đại, dễ dàng mang theo và sử dụng trong nhiều tình huống khác nhau. Với khối lượng nhẹ và kiểu dáng nhỏ gọn, tai nghe ôm sát tai mà không gây cảm giác khó chịu ngay cả khi đeo trong thời gian dài. Ống tai nghe được thiết kế độc đáo, không chỉ giúp cải thiện chất lượng âm thanh mà còn đảm bảo độ ổn định khi vận động. Bề mặt tai nghe được phủ lớp hoàn thiện mịn màng, chống bám vân tay, cùng với màu sắc trẻ trung và thanh lịch, tạo nên một phụ kiện thời trang không thể thiếu cho những ai yêu thích sự tiện lợi và phong cách.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
