new GadgetDescription { Id = Guid.Parse("19bf748d-f1aa-4b7c-bae1-4fbfa3aa6fb9"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "Hệ thống chống ồn linh hoạt, âm thanh chất lượng và sống động", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("f9e44bca-721d-49d3-b2f6-54823bcc7125"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "Hãng đã trang bị tính năng Khử ồn thông minh theo bối cảnh cho sản phẩm. Với tính năng này, thiết bị sẽ tự động điều chỉnh mức độ khử ồn theo môi trường xung quanh. Mang đến trải nghiệm yên tĩnh và thoải mái hơn cho người dùng mà không cần thay đổi thủ công.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("d869aa22-df37-4ab7-88e2-ae09e915360e"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "Bên cạnh khả năng khử ồn hiệu quả thì, chế độ xuyên âm trên sản phẩm sẽ cho phép bạn duy trì kết nối với môi trường xung quanh dễ dàng hơn. Với các micro được tích hợp trong thiết bị, tai nghe True Wireless OPPO sẽ thu và khuếch đại âm thanh xung quanh, giúp bạn nghe rõ tiếng còi xe, tiếng rao bán hàng và tiếng nói chuyện mà không cần tháo tai nghe.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("15d551f7-4322-40d5-81de-ad3da384b370"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/74af3be7-6642-40a0-893a-4ca656e95729.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("0bf08e14-b899-4a1b-8570-7222ce03b105"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "Nhiều người yêu âm nhạc luôn mong muốn thưởng thức những bản nhạc với chất lượng âm thanh tốt nhất, sống động và chân thực. Để đáp ứng nhu cầu này, OPPO đã trang bị cho sản phẩm công nghệ LHDC 5.0 tiên tiến và âm thanh Hi-Res chất lượng, mang đến khả năng tái tạo âm thanh chi tiết và chính xác như nghe từ đĩa CD hoặc file nhạc lossless.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("4198174e-8f77-421b-92f9-102cd2342c31"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "Kết nối ổn định, thao tác dễ dàng", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("e5f5d90a-07c0-4a53-a530-c6a71d765de2"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "Tai nghe OPPO sở hữu công nghệ Bluetooth 5.4 mới nhất, có độ trễ âm thanh chỉ 47 ms, mang lại khả năng chống nhiễu vượt trội và cải thiện độ ổn định kết nối. Nhờ đó, bạn sẽ trải nghiệm kết nối nhanh chóng và liên tục, không bị gián đoạn. Công nghệ này đảm bảo âm thanh luôn mượt mà, không bị ảnh hưởng bởi yếu tố ngoại vi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("e313065d-b7cd-41b6-b984-4742008e06b4"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "Tai nghe còn hỗ trợ chụp ảnh nhanh chóng chỉ với hai lần chạm, khi sử dụng điện thoại OPPO chạy hệ điều hành ColorOS 11.3 trở lên, giúp người dùng không bỏ lỡ bất kỳ khoảnh khắc đáng nhớ nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("0b22fcbd-dc0c-44b8-8518-84e216ad6892"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "An tâm hơn với dung lượng pin và chuẩn kháng bụi, nước tốt", Type = ParseGadgetDescriptionType("BoldText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("1553ad52-9091-4450-adba-7d1f95513fed"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "Với hộp sạc và tai nghe True Wireless, bạn có thể thưởng thức âm nhạc liên tục đến 44 giờ mà không lo hết pin. Nếu cần, chỉ cần sạc trong 1 tiếng là bạn có thể sử dụng thêm 12 giờ nữa. Điều này hỗ trợ những người bận rộn và giúp bạn không bỏ lỡ bất kỳ giai điệu nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("76e48df0-db82-41e8-a81c-07945eaf06fc"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "Sản phẩm còn có khả năng kháng bụi và nước chuẩn IP55, bảo vệ thiết bị khỏi bụi hay áp suất nước từ vòi nước, tạo sự an tâm cho người dùng khi đeo dưới mưa nhỏ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("d0e3f1f1-e30f-41a8-990a-06812a629894"), GadgetId = Guid.Parse("d207cb52-20bd-4682-a804-f1dde735409e"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/027df4e4-84a4-4cf9-a2d5-7307584e56b8.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("9e6685fd-c398-4aa8-b4ff-52d38b2fa3ab"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Thả mình vào các bài nhạc yêu thích với tai nghe Bluetooth True Wireless OPPO Enco Buds 2 Pro E510A sở hữu thiết kế nhỏ gọn cùng vẻ ngoài thời thượng, được tích hợp nhiều tính năng hấp dẫn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("9871bd61-bb94-4e7d-8eb7-b2d4b639ac6b"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Thiết kế thời trang, đề cao sự thoải mái", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("f95a2d1b-0649-4021-ae3c-06c3441dbe09"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Tai nghe True Wireless này được thiết kế công thái học, mang đến trải nghiệm đeo tai thoải mái suốt cả ngày. Với kiểu dáng in-ear vừa vặn, cùng đệm tai làm bằng silicon mềm mại, cho khả năng ôm sát tai, tạo cảm giác chắc chắn mà không gây đau nhức.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("eaeb69e0-8315-4212-a966-4a86f5bbba3e"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/173ba672-77f3-436c-bf51-4e438afdd05d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("73f36072-dee9-4038-a9d5-4f287d701a44"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Cho trải nghiệm nghe tối ưu, tiện lợi", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("13997f5c-6ff1-42ee-9d1e-69f312e75ac1"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Được kết hợp cả Dirac Audio Tuner và Dolby Atmos, OPPO Enco Buds 2 Pro mang đến một trải nghiệm âm thanh đỉnh cao, vượt xa những gì mà một chiếc tai nghe true wireless thông thường có thể làm được. Người dùng sẽ được đắm chìm trong không gian âm nhạc sống động, với âm bass sâu lắng, âm trung ấm áp và âm cao trong trẻo, chi tiết. Mang đến cho người dùng những trải nghiệm âm nhạc sống động và chân thực.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("a892edf4-30c6-4861-a5ea-759d195f9cba"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "OPPO Buds này được thiết kế để mang đến trải nghiệm đàm thoại tuyệt vời ngay cả trong môi trường nhiều gió. Với vị trí đặt micro được tối ưu hóa và ống dẫn âm thông minh, tai nghe giảm thiểu tối đa tiếng rít và nhiễu do gió gây ra. Cho dù bạn đang đi xe đạp, chạy bộ hay đơn giản chỉ là đi dạo ngoài trời, tai nghe đều là người bạn đồng hành đáng tin cậy của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
