new GadgetDescription { Id = Guid.Parse("a39bfbe4-e72c-442c-ba23-b04ff75ec83f"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Kết nối nhanh chóng, ổn định cho độ trễ thấp", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("fe6a29c5-04e7-44f3-b27f-c6802ef3aa5d"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Tai nghe In-ear được trang bị công nghệ Bluetooth 5.3 tiên tiến, mang đến trải nghiệm kết nối không dây mượt mà và ổn định hơn bao giờ hết. Với khả năng kết nối nhanh chóng, phạm vi phủ sóng rộng, giúp bạn có thể tự do di chuyển mà không lo bị ngắt kết nối.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("4fd7230c-c5b1-4587-a702-384b107f164a"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Độ trễ thấp của Buds 2 giúp âm thanh và hình ảnh được đồng bộ với nhau, mang đến trải nghiệm xem phim sống động như đang ở rạp. Bạn sẽ không còn cảm thấy khó chịu vì âm thanh bị trễ so với hình ảnh nữa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("90d8fbe9-4441-4584-b4c8-c4fe97b62d5b"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Không lo gián đoạn với năng lượng bền bỉ và chuẩn IP55", Type = ParseGadgetDescriptionType("BoldText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("7732139a-d84e-4bec-bbc0-99b2c9ccf1d2"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Không còn lo lắng về việc hết pin giữa chừng, Buds 2 Pro sẽ mang đến cho bạn sự tự do tận hưởng âm nhạc mọi lúc mọi nơi. Với thời lượng pin lên đến 8 tiếng cho mỗi bên tai và tổng cộng 38 giờ khi sử dụng cùng hộp sạc, thiết bị cho khả năng đồng hành bền bỉ bên bạn suốt cả ngày dài, từ phòng tập gym đến những chuyến đi xa, không lo gián đoạn trải nghiệm âm nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("d0770fbf-a160-4d2c-87bd-5e8d5bccae58"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/d424fc22-bb38-4775-80d4-9773578beb71.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("638301fa-59a7-4683-baeb-9b088623c772"), GadgetId = Guid.Parse("be21e746-8e96-4f21-b562-e1c78a12506b"), Value = "Tai nghe OPPO với tiêu chuẩn kháng nước và bụi IP55 cho phép bạn thoải mái tận hưởng âm nhạc trong mọi điều kiện, từ phòng tập gym đổ mồ hôi đến những buổi chạy bộ ngoài trời dưới mưa nhỏ. Bạn có thể yên tâm rằng tai nghe sẽ hoạt động ổn định, không lo bị hư hỏng bởi mồ hôi hay bụi bẩn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
