new GadgetDescription { Id = Guid.Parse("d198dba4-4758-4383-90b7-88674c48c90d"), GadgetId = Guid.Parse("4502beea-7670-423e-86db-65428ba6eca4"), Value = "Giọng thoại rõ ràng, người nghe và người nói trao đổi thông tin thuận lợi thông qua micro trên tai nghe, nhờ đó bạn có thể thoải mái trò chuyện cùng khách hàng, đối tác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("15cd9eb0-d289-4474-b944-c781e77adbc5"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Vào tháng 01/2023, nhà OPPO đã giới thiệu dòng tai nghe mới OPPO ENCO Air 3, phiên bản kế nhiệm của chiếc OPPO ENCO Air 2. Mang đến một kiểu thiết kế vừa quen thuộc lại vừa lạ mắt cùng nhiều tính năng được nâng cấp nhằm mang đến trải nghiệm sử dụng tốt nhất cho người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("fd58f2ee-bd38-48cd-a5a4-56369ad513bf"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Cải tiến lớn ở ngoại hình với vẻ ngoài cực sang chảnh", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("cb29d831-d70c-4bcc-8353-436ddc2bbce0"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Có thể nói ngoài phần nắp gập nhựa bóng mờ đặc trưng của dòng ENCO Air được giữ nguyên thì toàn bộ thiết kế đều được làm mới. Chúng ta có một chiếc tai nghe tròn trịa hơn, nhỏ gọn hơn so với kích thước của phiên bản tiền nhiệm, kết hợp cùng màu sắc thời trang mang lại cảm giác sang trọng hơn. Theo đánh giá cá nhân của mình, sự cải tiến lần này đem đến một diện mạo thu hút hơn hẳn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("5f2ac809-2ead-43f7-acf2-305abc15b386"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Phần nắp gập được hoàn thiện bằng nhựa bóng mờ, có thể nhìn thấy khá rõ tai nghe ở trong. Thêm vào đó, phần nhựa mờ này cũng sẽ giúp che tốt các vết trầy xước và dấu vân tay bám trên tai nghe, cảm giác tai nghe vẫn luôn sạch sẽ và mới mẻ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("4d5867a6-1427-4026-8854-fcc770c376a3"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Thay vì chứa toàn bộ housing của tai nghe trong hộp sạc như thiết kế trước, ở phiên bản này nắp gập lớn hơn nên housing của tai nghe cũng chỉ gắn một nửa vào hộp sạc. Tuy nhiên không vì vậy mà tai nghe lỏng lẻo, dễ rớt vì trên hộp sạc và tai nghe đều có tích hợp nam châm giúp dính hút chắc chắn. Mình thử lắc mạnh nhiều lần nhưng tai nghe vẫn không hề có dấu hiệu rơi ra ngoài. Thêm vào đó, chính kiểu thiết kế mới lạ này còn giúp mình gỡ tai nghe ra dễ dàng hơn hẳn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("d51bfb7b-f68e-4ab4-8943-70bb3b65586f"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/9160bba9-8979-451b-b4a1-540c400df904.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("eb8b57f8-d9ad-4381-95b9-c5ddeef5fb11"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Phần nắp gập và bản lề cứng cáp hơn, có thể mở ra 110 độ, cảm giác khi mình đóng mở cũng chắc chắn hơn. Trên hộp sạc có trang bị thêm một chấm LED nhỏ giúp báo pin và tình trạng kết nối. Khi pin đầy đèn LED sẽ báo xanh, khi pin vừa đèn báo vàng và khi pin yếu đèn báo đỏ. Còn khi thể hiện tình trạng kết nối, pin sẽ nháy trắng nếu đang dò tìm thiết bị. Khi kết nối xong, đèn sẽ sáng xanh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("fd4bb0c6-a85f-4550-b801-bab4b887df84"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Vẫn là dòng tai nghe Earbuds, nhưng thiết kế đã được thay đổi một chút: Housing to hơn và viền ngoài housing được ghép một lớp nhựa trong giúp tạo hiệu ứng giọt nước đẹp mắt. Khi mình đưa đuôi tai nghe lên trước ánh đèn nó sẽ phản chiếu ánh sáng và tạo cảm giác như giọt sương sớm bám trên những tán lá mỗi buổi sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("7eab9ac8-8c9e-4e33-8ca6-40df12c10b34"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1170994b-e3a9-47cf-b405-abf3b0111c3c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("e16474e8-13ed-45e5-9a38-2ab8fec57b40"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Kết nối nhanh chóng, điều khiển dễ dàng", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("6a93f116-4ed1-449b-83aa-d47bc8a89279"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Sau khi mở Bluetooth trên thiết bị, bạn chọn kết nối với tên ''OPPO Enco Air 3\", khi hoàn tất kết nối, đèn báo trên hộp sạc sẽ sáng xanh. Trường hợp tai nghe đang kết nối với thiết bị khác mà bạn muốn huỷ kết nối để thay thế thiết bị thì có thể nhấn giữ khoảng 4 giây ở cả 2 tai nghe đến khi nghe tiếng báo hiệu là được, sau đó kết nối lại như bình thường. Nếu muốn cài tai nghe về thiết lập gốc thì bạn chỉ cần nhấn giữ 2 tai nghe trong vòng 15 giây.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("214377b6-e6c5-4401-9ef3-21e251029cb0"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/dddc1aa6-8a5f-40f9-9178-26bedd7287f7.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("ccab3f48-3064-4077-9a36-d07fa8bf45f4"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Đặc biệt, ở phiên bản mới này, OPPO trang bị thêm chức năng Dual connection cho phép tai nghe kết nối được cùng lúc 2 thiết bị. Theo đó, sau khi kết nối, thiết bị nào mở âm thanh, tai nghe sẽ phát âm thanh trên thiết bị đó, điều này giúp mình luân chuyển thiết bị sử dụng nhanh hơn, xử lý công việc hiệu quả hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("e370efc8-83f3-4193-b1af-9fc5d1c403a1"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Nâng cấp với công nghệ mới cho âm thanh đã tai hơn", Type = ParseGadgetDescriptionType("BoldText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("d69f7632-df9e-4aa0-96fa-68945fe203ea"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Một nâng cấp mà mình nghĩ là đáng tiền nhất ở chất âm của chiếc tai nghe True Wireless này đó là phần âm thanh vòm OPPO Alive Audio. Khi kích hoạt tính năng này lên sẽ cho phép tai nghe giả lập âm thanh 3 chiều, cộng thêm việc đeo lâu không bị bí tai giúp mình đôi khi quên mất đang đeo tai nghe vì cảm giác như âm thanh đang phát ra từ một chiếc loa nào quanh đây.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("a769be78-4947-4353-bacd-cc997ce7923c"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Nhìn chung, âm bass chưa thể gọi là xuất sắc nhưng vẫn đủ độ sâu và đầm để khi nghe các bản nhạc nhiều bass như EDM, rap,... không bị mất quá nhiều chi tiết, âm thanh vẫn cực kỳ sống động. Mình vẫn hoàn toàn có thể nghe được các nhịp trống trong các bài nhạc hay tiếng bước chân, âm thanh dồn dập khi xem phim. Nếu bạn cảm thấy âm bass vẫn chưa đủ dày, có thể kích hoạt chế độ Bass Boost trong phần cài đặt trên ứng dụng để tăng cường.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("fb8f19e0-6e39-4e1c-9523-5be0347ada66"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Về âm treble, phần thể hiện có lẽ sẽ kém hơn âm bass một chút, tuy vậy vẫn đủ trong trẻo để nghe các bản nhạc ballad hay các dòng nhạc nhẹ khác. Đặc biệt tai nghe không hề bị chói ở các nốt cao và giọng ca sĩ khá rõ ràng, đáp ứng tốt các nhu cầu nghe nhạc của một người dùng cơ bản như mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("e5ea3a35-657b-4bd2-879b-4026cbe90fa5"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Tối ưu lượng điện năng tiêu thụ, gia tăng thời gian sử dụng", Type = ParseGadgetDescriptionType("BoldText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("f16fe161-cf08-44aa-800c-fc5ebf208458"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Theo thông tin mà OPPO cung cấp, so với phiên bản tiền nhiệm thì OPPO ENCO Air 3 có sức mạnh xử lý cao hơn 25 lần, mức tiêu thụ điện năng thấp hơn 35% và thời lượng pin dài hơn 50%. Với mức sử dụng của mình một ngày khoảng 6 tiếng, khi dùng kết hợp với hộp sạc sẽ cho tổng thời gian sử dụng khoảng 25 tiếng (khoảng hơn 4 ngày một chút). Nhìn chung, trải nghiệm nghe nhạc của mình đã liền mạch hơn chiếc ENCO Air 2 trước đó, đặc biệt là đối với những người dùng cần đeo tai nghe thường xuyên sẽ cảm nhận rõ rệt hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("fd83501b-fc3a-4459-a873-b80aa0dc3697"), GadgetId = Guid.Parse("b18df922-bcc1-4065-ab28-2df9343631de"), Value = "Khi tai nghe hết pin, bạn chỉ cần bỏ tai nghe vào hộp sạc, ngay lập tức tai nghe sẽ được cung cấp điện năng cho lần sử dụng tiếp theo. Còn khi hộp sạc hết pin thì bạn cắm sạc thông qua cổng USB trang bị ở đáy hộp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("f00ffb89-f718-4dd7-a75e-eba864f63300"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 là chiếc tai nghe mới được ra mắt của nhà OPPO vào tháng 8/2022, chỉ vài tháng sau khi phiên bản tiền nhiệm của nó là OPPO ENCO Air 2 ra đời. Đến với phiên bản Pro này, nhà OPPO đã có vài nâng cấp mà mình rất thích. Vậy những điểm thu hút đó là gì?", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("ca6d23de-6363-4366-af28-553c08351076"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Thiết kế gọn gàng, kiểu dáng sang trọng, màu sắc trang nhã", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("069f5d8c-2d72-4aab-9bb5-d2f5e24952c4"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Thay vì thiết kế dạng nắp hộp trong suốt như trước kia, chiếc tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 này mang đến thiết kế hộp đựng được bao kín hoàn toàn tạo cảm giác chắc chắn. Các chi tiết thiết kế như bản lề, đường bo góc mình nhận thấy chiếc ENCO Air 2 Pro này vẫn được chăm chút tỉ mỉ. Bên ngoài hộp sạc sẽ gồm có:", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("39257864-d9ff-48d1-9251-bdafc47bc8b6"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "- Đèn báo pin ngay phía dưới nắp hộp có thể hiển thị 3 màu sắc trạng thái pin: Đỏ (Pin yếu, sắp hết pin) - Vàng (Mức pin vừa) - Xanh lá (Pin ở trạng thái đầy). Cụ thể, khi mở nắp hộp ra (đảm bảo tai nghe nằm trong hộp sạc), màu đèn báo sẽ thể hiện lượng pin còn lại của tai nghe. Khi đóng hộp sạc lại màu đèn báo sẽ thể hiện lượng pin còn lại của hộp sạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("ca107723-3559-442a-b6b5-040064cecd43"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "- Khe cắm sạc cho hộp đựng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("8d4bf4a2-5065-4a8e-94ba-a6c8adb8add3"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "- Mặt sau có tên nhãn hiệu trông thì đơn giản nhưng lại vô cùng đẹp mắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("7c284749-28d2-470a-9d55-5fb9ff67f2c7"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/5c47d0c5-bbf3-4fd1-800a-b30c8374db59.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("c7221800-7da8-41f7-8ce0-4846a0f44574"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Phần bản lề khá chắc chắn, đóng mở dễ dàng chỉ bằng một tay. Ngoài ra, mình có thể nghe được tiếng \"tách\" khi đóng hộp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("1797b172-6392-4af7-8f0e-ee7a65943c4b"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Tổng thể chiếc hộp sạc, mình chỉ có 2 từ “tuyệt vời” để diễn tả: Kiểu dáng đẹp mắt, màu sắc nhã nhặn, thiết kế bo tròn gọn nhẹ dễ cầm, dễ nắm trong lòng bàn tay. Với thiết kế này, mình hoàn toàn có thể bỏ túi áo hoặc balo và mang đi bất kì đâu, vô cùng tiện lợi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("7b78d8f4-81cc-494a-bcc1-357ec8c2d5e5"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Tuy nhiên, phần hộp được làm từ nhựa bóng, đẹp thì có đẹp, nhưng nếu cầm nắm nhiều có thể sẽ bám dính một chút dấu vân tay. Nếu muốn bảo vệ tốt hơn cho hộp sạc bạn có thể trang bị thêm case đựng chuyên dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("e5f1c4a5-af41-4624-ad80-7899a27c08c3"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Về phần tai nghe, khoan hãy nói về chất âm và các yếu tố trải nghiệm khác, chỉ riêng phần thiết kế tai nghe mình đã rất hài lòng. Lần này, OPPO mang đến một chiếc tai nghe In-ear rất “vừa lòng” những con dân chỉ hợp với tai nghe dạng In-ear như mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("f79e926f-6026-4c2f-90ee-54a592b04745"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Âm thanh sống động, dải âm rõ ràng, chi tiết", Type = ParseGadgetDescriptionType("BoldText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("c2cad76b-a91a-4d32-a467-e8d2a612cf9c"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Tiêu chí tiếp theo vô cùng quan trọng, đó là chất âm phát ra từ tai nghe. Theo mình cảm nhận âm thanh khá rõ ràng, khi mình nghe những bản nhạc có nhiều âm bass như EDM, mình có thể nghe được từng nhịp trống, âm bass gọn gàng, chắc khoẻ chứ không hề làm mất đi độ chi tiết của âm trầm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("cfb3a095-bd4a-42f3-abfd-796ab9bef95d"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Với khả năng này, mình xem những bộ phim bom tấn với nhiều tình tiết dồn dập cũng không phải vấn đề. So với chiếc ENCO Air 2 trước đó thì phiên bản Pro này mình đánh giá là cho khả năng thể hiện nhiều âm bass hơn hẳn, âm bass cũng dày và chắc khỏe hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("faf006a0-2929-472f-afdd-1f0bb1ea0fe5"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Khi sử dụng chung với app, mình có thể điều chỉnh linh hoạt với 3 bộ chỉnh âm. Tuy nhiên, âm thanh từ 3 bộ chỉnh âm này, theo mình cảm nhận thì không có nhiều sự khác biệt lắm. Có một chế độ đặc biệt đó là Game Mode, khi kích hoạt chế độ này sẽ cho phép âm thanh truyền đến nhanh chóng, mượt mà, đưa độ trễ gần như về con số 0 giúp hỗ trợ tối đa trong quá trình chơi game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("7a0bb073-dd42-4052-86cb-992052366f4c"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Hỗ trợ mic đàm thoại tiện dụng", Type = ParseGadgetDescriptionType("BoldText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("eb960259-24e6-4f8b-b88a-0080622c2596"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Bên cạnh âm thanh rõ ràng, chân thực thì trên mỗi chiếc tai nghe đều được gắn micro đàm thoại cho chất lượng âm thanh thu được rõ ràng, mượt mà. Mình có thể thoải mái nghe, gọi thực hiện các cuộc gọi qua tai nghe chỉ bằng cách nhấn đúp vào 1 bên tai nghe khi có cuộc gọi thoại tới.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
