new GadgetDescription { Id = Guid.Parse("7478a8d5-62c5-4814-a562-31406ced995b"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Công nghệ Bluetooth 5.2 hiện đại cho kết nối mượt mà, ổn định", Type = ParseGadgetDescriptionType("BoldText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("1b141bcf-4bc4-4218-b158-1bfbcff6329d"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Với lần kết nối đầu tiên, bạn cần thực hiện các thao tác như dò, tìm tai nghe và nhấn “Connect” để kết nối tai nghe và thiết bị. Khi ghép nối thành công, OPPO ENCO Air 2 Pro có thể ghi nhớ thiết bị và tự động kết nối từ lần thứ 2 trở đi ngay khi bạn mở hộp sạc (bạn cần đảm bảo thiết bị ghép nối đã mở sẵn Bluetooth nhé).", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("f080be1a-a066-4df7-afd9-0563e9b5bdcd"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Chống nước chuẩn IP54", Type = ParseGadgetDescriptionType("BoldText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("eb0eb72c-439e-4d32-9f5d-2a4e5e4b8f9f"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Theo mình đây là một tính năng vô cùng tiện lợi đặc biệt với mình hoặc những bạn thường xuyên ra ngoài có thể gặp thời tiết xấu như những cơn mưa bất thường, môi trường bụi bẩn hoặc những bạn dùng để đi tập thể dục, thể thao luôn cần một chiếc tai nghe có thể chống nước, chống mồ hôi hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("303eeff5-a2b7-4774-80f4-4bfb87d5e24b"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e22cc9cd-57b6-4d37-bfd7-9f75ec00163f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 22 },
new GadgetDescription { Id = Guid.Parse("fab0112d-50ef-432e-9160-2629ebe4b3e6"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Thời gian sử dụng, cổng sạc Type-C", Type = ParseGadgetDescriptionType("BoldText"), Index = 23 },
new GadgetDescription { Id = Guid.Parse("e7e2b287-a1f7-4562-8228-2a3004dfb444"), GadgetId = Guid.Parse("83f3ff81-0388-4795-b153-ac36868cdea4"), Value = "Nếu bạn đang băn khoăn về thời gian sử dụng của một chiếc tai nghe Bluetooth thường chỉ tầm 4 - 5 tiếng thì đừng lo lắng nữa, chiếc tai nghe OPPO ENCO Air 2 Pro có thể cho thời gian sử dụng liên tục tới 6 - 7 tiếng. Mình đã thử sử dụng nghe nhạc, xem phim liên tục và kết quả là thời gian sử dụng này thực sự khiến mình ấn tượng. Nếu bạn dùng chung với hộp sạc thì thời gian sử dụng sẽ còn lâu nữa, có thể đạt đến 2- 3 ngày với mức sử dụng bình thường.", Type = ParseGadgetDescriptionType("NormalText"), Index = 24 },
new GadgetDescription { Id = Guid.Parse("1bf11728-82d0-486f-a51b-52654d9f2dad"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Tận hưởng từng nhịp điệu ưa thích của bạn với Galaxy Buds 3 - ra mắt vào tháng 7/2024, đưa âm thanh đến gần tai bạn hơn với công nghệ ấn tượng cùng khả năng phiên dịch nhanh gọn của Galaxy AI, đưa đến một sản phẩm vừa bắt kịp xu hướng công nghệ lại vô cùng tiện dụng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("802cf58d-a8ad-482a-b100-731196c5f988"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Thay vì các phiên bản Buds trước được làm theo kiểu hộp nhẫn thì giờ đây, Buds3 đã được thiết kế dạng mở open-type hoàn toàn mới với cốc đựng bóng bẩy, hiện đại. Tai nghe được làm với các góc cạnh được bo tròn cùng phần nắp trong suốt để hỗ trợ người dùng nhìn được tai nghe bên trong.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("3e370ff0-5e21-404b-8f7e-04b3a99d015d"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1b8bf0ee-c7f0-487c-ade2-ee032c5886bc.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("0afa6ad0-5c21-4595-a75c-970b7f73ba9f"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Tai nghe được chế tạo tỉ mỉ dựa trên dữ liệu tai 3D, đem đến một thiết kế công thái học vừa vặn và thoải mái, giúp giảm thiểu áp lực lên ống tai, cho khả năng đeo liên tục nhiều giờ mà không gây khó chịu cho người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("c26f97ab-0b86-4b6f-a7cb-0e55d59f7242"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Rào cản ngôn ngữ không còn là vấn đề với Galaxy AI", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("6ee82127-c58b-4e70-a5bc-6a7cbe801871"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Ở lần ra mắt phiên bản này, Samsung đã mang đến cho người dùng một tiện ích có thể hỗ trợ bạn nói chuyện với người bản địa hoặc trợ giúp trong việc học ngoại ngữ mới với tính năng Interpreter (phiên dịch trực tiếp), cho phép người dùng đồng thời nghe và xem bản dịch của cuộc trò chuyện thông qua tai nghe và điện thoại Samsung Galaxy các dòng S24, S23, Z Fold 5, Z Flip 5, S23 FE, S22, Z Fold 4, Z Flip 4. Chỉ cần vài thao tác đơn giản để đăng nhập vào Samsung Account và bạn sẽ tự tin giao tiếp cùng người nước ngoài ở những nơi bạn đặt chân đến.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("9fd985d2-d907-4f87-8cfb-ee6d7238e00b"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/2fcfcf82-63d8-454f-893f-83a037ef50cc.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("ad00a53c-c734-428c-815f-b2cbeac770b1"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Đem đến chất âm trung thực, sống động", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("fd61df5b-3568-4c23-bc5d-78c7e6b3aa5b"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Đắm chìm trong âm thanh chất lượng cao chuẩn studio với tai nghe True Wireless, cho độ phân giải âm thanh lên đến 24 bit trên các điện thoại Galaxy dòng S24, S23, Z Fold 6, Z Flip 6, Z Fold 5, Z Flip 5 và Tab S9 với One UI 6.1.1 trở lên. Mang đến chất âm trung thực, giúp bạn nghe được các chi tiết âm phức tạp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
