new GadgetDescription { Id = Guid.Parse("0e5c613e-04ed-4229-b7ce-a2b1139fa2fe"), GadgetId = Guid.Parse("be17d34b-110c-4bce-8d7b-a58849339d2b"), Value = "Hạn chế tiếng ồn từ môi trường xung quanh cùng Samsung Galaxy Buds 3 Pro với công nghệ chống ồn chủ động (ANC), bằng cách tạo ra các tín hiệu đối lập để hủy tiếng ồn, công nghệ này sẽ ngăn chặn các âm thanh và tạp âm bên ngoài, giúp người dùng cảm nhận và thưởng thức âm thanh từ tai nghe tốt hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
