new GadgetDescription { Id = Guid.Parse("463a6ca7-814d-43c5-ab91-18c31b9ff23f"), GadgetId = Guid.Parse("be17d34b-110c-4bce-8d7b-a58849339d2b"), Value = "Nâng tầm trải nghiệm với những tính năng hữu ích khác", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("e1a294d7-a67d-4770-94a2-3fae372444e8"), GadgetId = Guid.Parse("be17d34b-110c-4bce-8d7b-a58849339d2b"), Value = "Galaxy Buds 3 Pro còn sở hữu tính năng Auto Switch, giúp tai nghe linh hoạt kết nối với nhiều thiết bị khác nhau, bao gồm Galaxy Watch 4 trở lên, Samsung Galaxy với One UI 6.0 trở lên, và các thiết bị Galaxy Tab có One UI 4.1.1 trở lên. Điều này giúp bạn dễ dàng chuyển đổi giữa các thiết bị một cách nhanh chóng khi cần thiết, mang đến trải nghiệm chơi game và làm việc không bị ngắt quãng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("7ca48ac7-cf0d-4c3f-b540-277caa2b6323"), GadgetId = Guid.Parse("4735ea2b-189a-4985-add2-bab79d7542ea"), Value = "Tai nghe Bluetooth True Wireless Samsung Galaxy Buds FE R400N là sự kết hợp hoàn hảo của thiết kế và chất âm khi mang diện mạo nhỏ gọn, khả năng tái tạo âm bass mạnh mẽ, công nghệ chống ồn chủ động, hỗ trợ đàm thoại rõ nét,... cùng vô vàn tiện ích khác chờ bạn khám phá, trải nghiệm.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("fe2594f0-3304-48ce-9747-99e52141a7e9"), GadgetId = Guid.Parse("4735ea2b-189a-4985-add2-bab79d7542ea"), Value = "Tai nghe Samsung Galaxy Buds FE sở hữu thiết kế công thái học, nằm gọn trong tai một cách vừa vặn, cho bạn cảm giác thoải mái, chắc chắn khi đeo. Hãng cũng tích hợp thêm 2 cặp nút chèn thay thế (wingtips) và 2 cặp đệm tai nghe (eartips) để bạn điều chỉnh kích cỡ cho vừa với khuôn tai của mình nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("a60b0830-068d-4463-9c94-4df0c99175be"), GadgetId = Guid.Parse("4735ea2b-189a-4985-add2-bab79d7542ea"), Value = "Với khả năng tái tạo âm bass sâu, mạnh mẽ từ loa 1 chiều mới, chiếc tai nghe True Wireless này sẽ giúp bạn tận hưởng những âm thanh dày và chân thực hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
