new GadgetDescription { Id = Guid.Parse("78b2a315-d995-472d-93c9-3da1ca253a1a"), GadgetId = Guid.Parse("1945cdf9-3703-43dd-9e34-053f8592b814"), Value = "Nút điều khiển màu vàng trên tai nghe được sử dụng như một nút đa chức năng với công dụng chuyển bài hát, phát/dừng chơi nhạc, tăng/giảm âm lượng, đồng thời cũng là nút nguồn, nút nhận/từ chối cuộc gọi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
