new GadgetDescription { Id = Guid.Parse("7de70b71-76ab-4634-bf0e-9de593b68b86"), GadgetId = Guid.Parse("1945cdf9-3703-43dd-9e34-053f8592b814"), Value = "Chất âm bùng nổ đậm bản sắc riêng", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a07a6706-9322-4998-991f-ede4579f734e"), GadgetId = Guid.Parse("1945cdf9-3703-43dd-9e34-053f8592b814"), Value = "Trái tim của Marshall Monitor II A.N.C. chính là Driver 40 mm mạnh mẽ, kết hợp cùng công nghệ âm thanh Marshall Signature Sound được tinh chỉnh tỉ mỉ, mang đến chất âm đặc trưng \"chất Marshall\" không lẫn vào đâu. Nhờ vậy, bạn sẽ được đắm chìm trong thế giới âm nhạc sống động với dải bass sâu lắng, âm trung rõ ràng và âm cao thanh thoát.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
