new GadgetDescription { Id = Guid.Parse("e51f950f-2717-4a71-9221-80ac8bd756d6"), GadgetId = Guid.Parse("d0ee451f-7197-4b04-90d7-5e9f03f52484"), Value = "Âm thanh được điều chỉnh tinh tế mang đến trải nghiệm thính giác đầy mới lạ, với chất âm cao trong trẻo và âm trung rõ ràng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
