new GadgetDescription { Id = Guid.Parse("527bac38-0391-4d7e-a63a-0a8f7653f601"), GadgetId = Guid.Parse("8fb0142d-1f86-4788-b66e-3959c6a44100"), Value = "Tai nghe Bluetooth Open - Ear True Wireless Soundpeats GoFree 2 thiết kế chuyên biệt cho việc tập luyện và tận hưởng âm nhạc, đáp ứng được các tiêu chí an toàn khi vận động mạnh hoặc tập luyện nơi đông người.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("37a6c1aa-1e4c-467d-be93-b7721cbfde26"), GadgetId = Guid.Parse("8fb0142d-1f86-4788-b66e-3959c6a44100"), Value = "Đạt chuẩn IPX5 có thể chống mưa nhẹ và mồ hôi, cho phép bạn tham gia các môn thể thao như chạy bộ, đạp xe, đi bộ đường dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
