new GadgetDescription { Id = Guid.Parse("f81c207f-ae7a-4ea9-8b94-409e5ff5d31b"), GadgetId = Guid.Parse("40476cb0-9f17-45c7-acb4-3cfcb30d3b1c"), Value = "Tai nghe Bluetooth True Wireless Soundpeats Clear-Pods sở hữu thiết kế trong suốt độc đáo, giúp sản phẩm nổi bật giữa các dòng tai nghe không dây khác trên thị trường. Chất liệu nhựa trong suốt kết hợp cùng các đường nét tinh tế mang lại vẻ ngoài sang trọng và hiện đại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
