new GadgetDescription { Id = Guid.Parse("7898d0c7-601e-4124-ab6d-87d87afe0404"), GadgetId = Guid.Parse("40476cb0-9f17-45c7-acb4-3cfcb30d3b1c"), Value = "Soundpeats Clear-Pods được trang bị màng loa sinh học 13 mm, mang đến chất lượng âm thanh đắm chìm và chân thực. Dù bạn đang nghe nhạc cổ điển, pop, rock hay EDM, tai nghe đều tái hiện âm thanh một cách chính xác, từ những nốt trầm sâu lắng đến những nốt cao trong trẻo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
