new GadgetDescription { Id = Guid.Parse("216fb668-c8f5-4c81-952f-7f22a5deaded"), GadgetId = Guid.Parse("3a53f00b-6f0a-4791-b9f4-0b8ab6d188ca"), Value = "Tai nghe Soundpeats Hearfit được trang bị Bluetooth 5.2, đảm bảo kết nối ổn định và nhanh chóng với các thiết bị di động. Bạn có thể nghe nhạc, nhận cuộc gọi hoặc điều khiển dễ dàng mà không bị gián đoạn, với phạm vi kết nối lên đến 10 mét.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("59b54cd1-a6f2-4fd5-998b-2c9a98cc514f"), GadgetId = Guid.Parse("3a53f00b-6f0a-4791-b9f4-0b8ab6d188ca"), Value = "Với pin dung lượng cao, tai nghe Soundpeats Hearfit cho phép bạn sử dụng liên tục lên đến 8 giờ chỉ với một lần sạc. Hộp sạc đi kèm cung cấp thêm thời gian sử dụng, giúp bạn dễ dàng mang theo và sử dụng bất cứ lúc nào, ở bất cứ đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("87e707af-830d-4bb7-a015-bfbe8bf9f009"), GadgetId = Guid.Parse("b1f9da60-49ba-4164-9b2c-bb0973179910"), Value = "Soundpeats RunFree Lite 2 được thiết kế dành riêng cho các hoạt động thể thao với kiểu dáng linh hoạt và nhẹ nhàng. Tai nghe ôm sát tai, không lo tuột khi vận động mạnh, giúp bạn tập trung vào việc rèn luyện mà không lo lắng về tai nghe. Đây là lựa chọn hoàn hảo cho người dùng yêu thích chạy bộ, tập gym hoặc các hoạt động ngoài trời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("6b31143e-85cb-4846-90d1-9daaf17a6e71"), GadgetId = Guid.Parse("b1f9da60-49ba-4164-9b2c-bb0973179910"), Value = "Với công nghệ âm thanh tiên tiến, RunFree Lite 2 mang đến trải nghiệm âm nhạc sống động và mạnh mẽ. Âm bass sâu lắng cùng âm trung và cao rõ ràng sẽ giúp bạn tận hưởng trọn vẹn các bài hát yêu thích trong suốt quá trình luyện tập mà vẫn giữ được sự tập trung.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("59c9c6cf-c818-4754-8efa-15291a9ee330"), GadgetId = Guid.Parse("b1f9da60-49ba-4164-9b2c-bb0973179910"), Value = "Soundpeats RunFree Lite 2 được trang bị công nghệ Bluetooth 5.3 hiện đại, cung cấp khả năng kết nối ổn định, mượt mà và phạm vi hoạt động rộng hơn. Với độ trễ thấp và kết nối không bị gián đoạn, bạn có thể dễ dàng nghe nhạc hoặc nhận cuộc gọi mà không gặp bất kỳ trở ngại nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("2898cca6-8fa3-41bb-8c28-cfa9fd15e7e7"), GadgetId = Guid.Parse("b1f9da60-49ba-4164-9b2c-bb0973179910"), Value = "Tai nghe Soundpeats RunFree Lite 2 có khả năng chống nước đạt chuẩn IPX5, chống mồ hôi và nước mưa, lý tưởng cho các hoạt động ngoài trời. Với pin dung lượng lớn, bạn có thể sử dụng liên tục trong 10 giờ, giúp bạn thoải mái trong suốt cả ngày mà không cần phải lo lắng về việc sạc lại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
