new GadgetDescription { Id = Guid.Parse("ff35125a-1d38-48c3-8cea-3fec42596bcf"), GadgetId = Guid.Parse("946fbab2-6468-4b14-bc85-2978d000f94b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/09c5839a-7553-4ea7-a114-d5e554649c63.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("42f538eb-56bd-4c11-a8a4-ebea9bb01dde"), GadgetId = Guid.Parse("123726f5-4b91-4a64-bbf9-02cfb9187893"), Value = "Tai nghe Bluetooth True Wireless Rezo Air 5 Deluxe ANC là sự kết hợp hoàn hảo giữa thiết kế hiện đại và công nghệ âm thanh tiên tiến. Với tính năng chống ồn chủ động (ANC), Rezo Air 5 Deluxe giúp bạn tận hưởng âm nhạc trong môi trường yên tĩnh, loại bỏ tiếng ồn xung quanh một cách hiệu quả. Chất âm sống động, bass mạnh mẽ cùng khả năng kết nối Bluetooth 5.2 nhanh chóng, ổn định, đảm bảo trải nghiệm nghe liền mạch.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("44a3583e-58c6-4a97-8244-a1e1a768b142"), GadgetId = Guid.Parse("123726f5-4b91-4a64-bbf9-02cfb9187893"), Value = "Thiết kế nhẹ nhàng, vừa vặn với tai giúp bạn thoải mái khi sử dụng trong thời gian dài. Hộp sạc nhỏ gọn, tích hợp sạc nhanh giúp tăng thời lượng sử dụng lên đến 30 giờ. Tai nghe cũng hỗ trợ điều khiển cảm ứng, tương thích với trợ lý ảo và khả năng chống nước chuẩn IPX4, phù hợp cho các hoạt động thể thao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("fcd7d24c-c6ce-4fd8-9e2b-2ded08797a5d"), GadgetId = Guid.Parse("123726f5-4b91-4a64-bbf9-02cfb9187893"), Value = "Rezo Air 5 Deluxe ANC là lựa chọn lý tưởng cho những ai đang tìm kiếm một tai nghe không dây cao cấp, đem lại trải nghiệm âm thanh đỉnh cao và tiện ích hiện đại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("5db83e2e-85c6-456d-9a63-2d531ab30ca3"), GadgetId = Guid.Parse("3f93ac18-701f-4731-85be-f702bdab7ab0"), Value = "Tai nghe Bluetooth True Wireless Rezo RZ09 ANC mang đến trải nghiệm âm thanh vượt trội với công nghệ chống ồn chủ động (ANC), giúp bạn đắm chìm trong không gian âm nhạc thuần khiết, không bị làm phiền bởi tiếng ồn xung quanh. Với chất lượng âm thanh rõ nét, dải âm cân bằng và âm bass sâu, Rezo RZ09 phù hợp cho cả những tín đồ âm nhạc khó tính.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("b25fab29-08f2-4a87-bbb8-8f37d8881b79"), GadgetId = Guid.Parse("3f93ac18-701f-4731-85be-f702bdab7ab0"), Value = "Kết nối Bluetooth 5.2 đảm bảo độ ổn định cao, không gián đoạn khi nghe nhạc hoặc đàm thoại. Thiết kế ergonomic giúp tai nghe vừa vặn và thoải mái khi đeo trong thời gian dài. Hộp sạc nhỏ gọn, cung cấp thời lượng pin lên đến 25 giờ, kết hợp cùng khả năng sạc nhanh giúp bạn luôn sẵn sàng sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("be4497e4-1739-4642-aad5-7073362509d3"), GadgetId = Guid.Parse("3f93ac18-701f-4731-85be-f702bdab7ab0"), Value = "Rezo RZ09 còn được trang bị khả năng điều khiển cảm ứng nhạy bén, hỗ trợ điều khiển trợ lý ảo và khả năng chống nước IPX5, đáp ứng tốt cho các hoạt động ngoài trời và luyện tập thể thao. Đây là sự lựa chọn hoàn hảo cho những ai tìm kiếm tai nghe không dây cao cấp với công nghệ tiên tiến và thiết kế thời thượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
