new GadgetDescription { Id = Guid.Parse("a77ee681-b4f1-4daa-83a7-2c120e6f80c4"), GadgetId = Guid.Parse("ca0709d7-50ff-41c8-94ea-d468acdb68a8"), Value = "Tai nghe Bluetooth True Wireless Soundcore K20i là sản phẩm lý tưởng cho những người yêu thích âm nhạc chất lượng cao với thiết kế hiện đại và tiện lợi. Soundcore K20i mang đến âm thanh sống động, rõ ràng với dải âm cân bằng và âm bass mạnh mẽ, giúp bạn trải nghiệm âm nhạc chân thực và cuốn hút.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("cf84670a-890e-4202-a10c-e7c5c7376115"), GadgetId = Guid.Parse("ca0709d7-50ff-41c8-94ea-d468acdb68a8"), Value = "Với kết nối Bluetooth 5.2, tai nghe đảm bảo độ ổn định cao, cho phép nghe nhạc và đàm thoại mượt mà mà không bị gián đoạn. Thời lượng pin lên đến 20 giờ với hộp sạc giúp bạn thoải mái sử dụng trong cả ngày dài. Thiết kế nhỏ gọn và ergonomic của Soundcore K20i mang lại sự thoải mái khi đeo, phù hợp cho các hoạt động di chuyển hay luyện tập thể thao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("1b0aea7c-1522-45ed-a001-fb19857d2748"), GadgetId = Guid.Parse("ca0709d7-50ff-41c8-94ea-d468acdb68a8"), Value = "Tai nghe còn có khả năng chống nước chuẩn IPX5, giúp bảo vệ khi sử dụng trong môi trường ẩm ướt hoặc mồ hôi khi vận động. Điều khiển cảm ứng thông minh giúp bạn dễ dàng quản lý cuộc gọi, âm nhạc và truy cập trợ lý ảo chỉ với một chạm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
