new GadgetDescription { Id = Guid.Parse("e54717c5-e38b-486a-a729-ba63e9f8ca49"), GadgetId = Guid.Parse("9d29b6d6-c7f1-450e-bb2b-8ce64d1d6290"), Value = "Tai nghe Bluetooth chụp tai Soundcore H30i là sự lựa chọn lý tưởng cho những ai tìm kiếm trải nghiệm âm thanh chất lượng cao và thoải mái trong thời gian dài. Với thiết kế over-ear (chụp tai) sang trọng, H30i mang lại cảm giác đeo êm ái, bao trọn tai, cách ly tiếng ồn hiệu quả, giúp bạn tập trung hoàn toàn vào âm nhạc.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
